    func testBulbFailures() {
        let bytes: [UInt8] = [0x0d, 0x00, 0x61, 0x01, 0x02, 0x00, 0x04, 0x01, 0x00, 0x01, 0x01, 0x02, 0x00, 0x04, 0x01, 0x00, 0x01, 0x05]
        
        guard let capability = try? AAAutoAPI.parseBytes(bytes) as? AADashboardLights else {
            return XCTFail("Could not parse bytes as `AADashboardLights`")
        }
        
        guard let bulbFailures = capability.bulbFailures?.compactMap({ $0.value }) else {
            return XCTFail("Could not get `.bulbFailures` values from `AADashboardLights` capability")
        }
        
        XCTAssertTrue(bulbFailures.contains { $0 == AADashboardLightsBulbFailures.turnSignalRight })
        XCTAssertTrue(bulbFailures.contains { $0 == AADashboardLightsBulbFailures.highBeam })
    }


