    func testGetDashboardLights() {
        let bytes: [UInt8] = [0x0d, 0x00, 0x61, 0x00]
        
        XCTAssertEqual(bytes, AADashboardLights.getDashboardLights())
    }
    
    func testGetDashboardLightsAvailability() {
        let bytes: [UInt8] = [0x0d, 0x00, 0x61, 0x02]
        
